import React, { useState } from "react";
import "./styles.css";
import "leaflet/dist/leaflet.css";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import MarkerClusterGroup from "react-leaflet-cluster";
import { Icon, divIcon, point } from "leaflet";
import { coordinatesData } from "./coordinates"; // Import coordinatesData from coordinates.js

// Define icons for different types
const supplierIcon = new Icon({
  iconUrl: require("./icons/supplier-icon.png"),
  iconSize: [38, 38]
});

const marketIcon = new Icon({
  iconUrl: require("./icons/market-icon.png"),
  iconSize: [38, 38]
});

const dryPortIcon = new Icon({
  iconUrl: require("./icons/dry-port-icon.png"),
  iconSize: [38, 38]
});

const originDestIcon = new Icon({
  iconUrl: require("./icons/origin-dest-icon.png"),
  iconSize: [38, 38]
});

const createClusterCustomIcon = function (cluster) {
  return new divIcon({
    html: `<span class="cluster-icon">${cluster.getChildCount()}</span>`,
    className: "custom-marker-cluster",
    iconSize: point(33, 33, true)
  });
};

const initialTextAreaValue = '';

export default function App() {
  const [textAreaValue, setTextAreaValue] = useState(initialTextAreaValue);

  const handleChange = (event) => {
    setTextAreaValue(event.target.value);
  };

  const markers = coordinatesData.map((coordinate, index) => ({
    geocode: [coordinate.latitude, coordinate.longitude],
    popUp: (
      <form>
        <label>
          Essay:
          <textarea value={textAreaValue} onChange={handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
    ),
    type: coordinate.type // Add type property to each marker
  }));

  return (
    <MapContainer center={[22.3149, 87.3105]} zoom={15}>
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      <MarkerClusterGroup
        chunkedLoading
        iconCreateFunction={createClusterCustomIcon}
      >
        {markers.map((marker, index) => (
          <Marker key={index} position={marker.geocode} icon={getIcon(marker.type)}>
            <Popup>{marker.popUp}</Popup>
          </Marker>
        ))}
      </MarkerClusterGroup>
    </MapContainer>
  );
}

// Function to get the appropriate icon based on the type
function getIcon(type) {
  switch (type) {
    case "Supplier":
      return supplierIcon;
    case "Market":
      return marketIcon;
    case "Dry Port":
      return dryPortIcon;
    case "Origin/Dest":
      return originDestIcon;
    default:
      return supplierIcon; // Default icon
  }
}
