import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import MarkerClusterGroup from "react-leaflet-cluster";
import { Icon, divIcon, point } from "leaflet";

const customIcon = new Icon({
  iconUrl: require("./icons/placeholder.png"),
  iconSize: [38, 38]
});

const createClusterCustomIcon = function (cluster) {
  return new divIcon({
    html: `<span class="cluster-icon">${cluster.getChildCount()}</span>`,
    className: "custom-marker-cluster",
    iconSize: point(33, 33, true)
  });
};

export default function App() {
  const [coordinates, setCoordinates] = useState([]);

  useEffect(() => {
    // Fetch coordinates data from JSON file
    fetch("/coordinates.json")
      .then((response) => response.json())
      .then((data) => setCoordinates(data))
      .catch((error) => console.error("Error fetching coordinates:", error));
  }, []);

  const markers = coordinates.map((coordinate, index) => ({
    geocode: [coordinate.latitude, coordinate.longitude],
    popUp: `Latitude: ${coordinate.latitude}, Longitude: ${coordinate.longitude}`
  }));

  return (
    <MapContainer center={[22.3149, 87.3105]} zoom={15}>
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      <MarkerClusterGroup
        chunkedLoading
        iconCreateFunction={createClusterCustomIcon}
      >
        {markers.map((marker, index) => (
          <Marker key={index} position={marker.geocode} icon={customIcon}>
            <Popup>{marker.popUp}</Popup>
          </Marker>
        ))}
      </MarkerClusterGroup>
    </MapContainer>
  );
}
