import React, { useState } from "react";
import "./styles.css";
import "leaflet/dist/leaflet.css";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import MarkerClusterGroup from "react-leaflet-cluster";
import { Icon, divIcon, point } from "leaflet";
import { coordinatesData } from "./coordinates"; // Import coordinatesData from coordinates.js

const customIcon = new Icon({
  iconUrl: require("./icons/placeholder.png"),
  iconSize: [38, 38]
});

const createClusterCustomIcon = function (cluster) {
  return new divIcon({
    html: `<span class="cluster-icon">${cluster.getChildCount()}</span>`,
    className: "custom-marker-cluster",
    iconSize: point(33, 33, true)
  });
};

const initialTextAreaValue = '';

export default function App() {
  const [textAreaValue, setTextAreaValue] = useState(initialTextAreaValue);
  const [filteredType, setFilteredType] = useState(null); // State to store filtered type

  const handleChange = (event) => {
    setTextAreaValue(event.target.value);
  };

  // Filter markers based on selected type
  const filteredMarkers = filteredType
    ? coordinatesData.filter((coordinate) => coordinate.type === filteredType)
    : coordinatesData;

  const handleTypeFilter = (type) => {
    setFilteredType(type);
  };

  return (
    <div className="map-container">
      <div className="button-container">
        <button onClick={() => handleTypeFilter("Supplier")}>Supplier</button>
        <button onClick={() => handleTypeFilter("Market")}>Market</button>
        <button onClick={() => handleTypeFilter("Dry Port")}>Dry Port</button>
        <button onClick={() => handleTypeFilter("Origin/Dest")}>Origin/Dest</button>
        <button onClick={() => setFilteredType(null)}>Show All</button>
      </div>
      <MapContainer center={[22.3149, 87.3105]} zoom={15} className="map">
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <MarkerClusterGroup
          chunkedLoading
          iconCreateFunction={createClusterCustomIcon}
        >
          {filteredMarkers.map((marker, index) => (
            <Marker key={index} position={[marker.latitude, marker.longitude]} icon={customIcon}>
              <Popup>{marker.Name}</Popup>
            </Marker>
          ))}
        </MarkerClusterGroup>
      </MapContainer>
    </div>
  );
}
