//     html: `<span class="cluster-icon">${cluster.getChildCount()}</span>`,
//     className: "custom-marker-cluster",
//     iconSize: point(33, 33, true)
//   });
// };

export default function Home() {

  const [selectedType, setSelectedType] = useState(null);
  const [quantityValue, setQuantityValue] = useState(1);
  const [selectedProduct, setSelectedProduct] = useState('Product 1');

  const [mapCenter,setMapCenter] = useState([23.6850, 90.3563]);
  const [mapZoom,setMapZoom] = useState(5);

  const [map,setMap]=useState(null);


  const RecenterAutomatically = ({lat,lng}) => {
    const map = useMap();
     useEffect(() => {
       map.setView([lat, lng]);
     }, [lat, lng]);
     return null;
   }


   <MapContainer center={[lat, lon]} zoom={80} scrollWheelZoom={true}>
        <TileLayer
          attribution='<a href="https://www.openstreetmap.org/copyright"></a>'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <Marker position={[lat,lng]}></Marker>
        <RecenterAutomatically lat={lat} lng={lng} />
</MapContainer>

  const handleSubmit = async (e, quantity, product, markerName) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://127.0.0.1:8000/your-backend-endpoint', {
        quantity: quantity,
        product: product,
        markerName: markerName
      });
      console.log(response.data);
    } catch (error) {
      console.error('Error:', error);
    }
  };



//  <MapContainer center={mapCenter} zoom={mapZoom} className="map-container">
//       <TileLayer
//         attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
