//             <Popup>
//               <div>
//                 <h3>{marker.name}</h3>
//                 <form onSubmit={(e) => handleSubmit(e, quantityValue, selectedProduct, marker.name)}>
//                   <label htmlFor="product">Select Product:</label>
//                   <select id="product" name="product" value={selectedProduct} onChange={(e)=>setSelectedProduct(e.target.value)}>
//                     <option value="Product 1">Product 1</option>
//                     <option value="Product 2">Product 2</option>
//                     <option value="Product 3">Product 3</option>
//                     <option value="Product 4">Product 4</option>
//                   </select>
//                   <br></br>
//                   <label htmlFor="quantity">Quantity:</label>
//                   <input type="number" id="quantity" name="quantity" min="1" value={quantityValue} onChange={(e)=>setQuantityValue(e.target.value)} />
//                   <button type="submit">Submit</button>
//                 </form>
//               </div>
//             </Popup>
//           </Marker>
//         ))}
//         <recenterAuto mapCenter={[coordinatesData.latitude,coordinatesData.longitude]}/>
//       {/* </MarkerClusterGroup> */}
//     </MapContainer>


  function setType(type){
      setSelectedType(type);
      setMapZoom(5);
      setMapCenter([23.6850, 90.3563]);
  }

  return (
    <div>
      <div className="button-container">
        <button onClick={() => setType("Supplier")}>Supplier</button>
        <button onClick={() => setType("Market")}>Market</button>
        <button onClick={() => setType("Dry Port")}>Dry Port</button>
        <button onClick={() => setType("Origin/Dest")}>Origin/Dest</button>
        <button onClick={() => setType(null)}>Show All</button>
      </div>
      {/* <input type='text'/>
      <div style={{
        position:"absolute",
        zIndex:1001,
        backgroundColor:"white"
      }}>
      {coordinatesData.filter(marker=>!selectedType || marker.type===selectedType).map((marker, index) => (
            <div key={index} onClick={()=>{
              setMapCenter([marker.latitude,marker.longitude]);
              setMapZoom(10);
            }}>
              {marker.name}
            </div>
          ))}
      </div> */}
      <select onChange={(e)=>{
              setMapCenter([coordinatesData.filter(marker=>!selectedType || marker.type===selectedType)[e.target.value].latitude,coordinatesData.filter(marker=>!selectedType || marker.type===selectedType)[e.target.value].longitude]);
              setMapZoom(10);
            }}>
      {coordinatesData.filter(marker=>!selectedType || marker.type===selectedType).map((marker, index) => (
            <option key={index} value={index}>
              {marker.name}
            </option>
          ))}
          </select>
          {/* Map is centered at {mapCenter[0]}, {mapCenter[1]} */}
          {map}
    </div>
  );
}

function getIcon(type) {
  switch (type) {
    case "Supplier":
      return supplierIcon;
    case "Market":
      return marketIcon;
    case "Dry Port":
      return dryPortIcon;
    case "Origin/Dest":
      return originDestIcon;
    default:
      return supplierIcon;
  }
}
