import { useEffect, useState } from "react";
import axios from "axios";
import "./styles.css";
import "leaflet/dist/leaflet.css";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
// import MarkerClusterGroup from "react-leaflet-cluster";
import { Icon, divIcon, map, point } from "leaflet";
import { coordinatesData } from "./coordinates";

const supplierIcon = new Icon({
  iconUrl: require("./icons/supplier-icon.png"),
  iconSize: [38, 38]
});

const marketIcon = new Icon({
  iconUrl: require("./icons/market-icon.png"),
  iconSize: [38, 38]
});

const dryPortIcon = new Icon({
  iconUrl: require("./icons/dry-port-icon.png"),
  iconSize: [38, 38]
});

const originDestIcon = new Icon({
  iconUrl: require("./icons/origin-dest-icon.png"),
  iconSize: [38, 38]
});


// const createClusterCustomIcon = function (cluster) {
//   return new divIcon({
//     html: `<span class="cluster-icon">${cluster.getChildCount()}</span>`,
//     className: "custom-marker-cluster",
//     iconSize: point(33, 33, true)
//   });
// };

export default function Home() {

  const [selectedType, setSelectedType] = useState(null);
  const [quantityValue, setQuantityValue] = useState(1);
  const [selectedProduct, setSelectedProduct] = useState('Product 1');

  const [mapCenter,setMapCenter] = useState([23.6850, 90.3563]);
  const [mapZoom,setMapZoom] = useState(5);

  const [map,setMap]=useState(null);

  const handleSubmit = async (e, quantity, product, markerName) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://127.0.0.1:8000/your-backend-endpoint', {
        quantity: quantity,
        product: product,
        markerName: markerName
      });
      console.log(response.data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const recenterAuto = ({mapCenter}) => {
    const map =useMap();
     useEffect(()=> {
      map.setMap([mapCenter]);
     },[mapCenter]);
     return null;
  }

 <MapContainer center={mapCenter} zoom={mapZoom} className="map-container">
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      {/* <MarkerClusterGroup
        chunkedLoading
        iconCreateFunction={createClusterCustomIcon}
      > */}
        {coordinatesData.filter(marker=>!selectedType || marker.type===selectedType).map((marker, index) => (
          <Marker key={index} position={[marker.latitude, marker.longitude]} icon={getIcon(marker.type)}>
            <Popup>
              <div>
                <h3>{marker.name}</h3>
                <form onSubmit={(e) => handleSubmit(e, quantityValue, selectedProduct, marker.name)}>
                  <label htmlFor="product">Select Product:</label>
                  <select id="product" name="product" value={selectedProduct} onChange={(e)=>setSelectedProduct(e.target.value)}>
                    <option value="Product 1">Product 1</option>
                    <option value="Product 2">Product 2</option>
                    <option value="Product 3">Product 3</option>
                    <option value="Product 4">Product 4</option>
                  </select>
                  <br></br>
                  <label htmlFor="quantity">Quantity:</label>
                  <input type="number" id="quantity" name="quantity" min="1" value={quantityValue} onChange={(e)=>setQuantityValue(e.target.value)} />
                  <button type="submit">Submit</button>
                </form>
              </div>
            </Popup>
          </Marker>
        ))}
        <recenterAuto mapCenter={[coordinatesData.latitude,coordinatesData.longitude]}/>
      {/* </MarkerClusterGroup> */}
    </MapContainer>


  function setType(type){
      setSelectedType(type);
      setMapZoom(5);
      setMapCenter([23.6850, 90.3563]);
  }

  return (
    <div>
      <div className="button-container">
        <button onClick={() => setType("Supplier")}>Supplier</button>
        <button onClick={() => setType("Market")}>Market</button>
        <button onClick={() => setType("Dry Port")}>Dry Port</button>
        <button onClick={() => setType("Origin/Dest")}>Origin/Dest</button>
        <button onClick={() => setType(null)}>Show All</button>
      </div>
      {/* <input type='text'/>
      <div style={{
        position:"absolute",
        zIndex:1001,
        backgroundColor:"white"
      }}>
      {coordinatesData.filter(marker=>!selectedType || marker.type===selectedType).map((marker, index) => (
            <div key={index} onClick={()=>{
              setMapCenter([marker.latitude,marker.longitude]);
              setMapZoom(10);
            }}>
              {marker.name}
            </div>
          ))}
      </div> */}
      <select onChange={(e)=>{
              setMapCenter([coordinatesData.filter(marker=>!selectedType || marker.type===selectedType)[e.target.value].latitude,coordinatesData.filter(marker=>!selectedType || marker.type===selectedType)[e.target.value].longitude]);
              setMapZoom(10);
            }}>
      {coordinatesData.filter(marker=>!selectedType || marker.type===selectedType).map((marker, index) => (
            <option key={index} value={index}>
              {marker.name}
            </option>
          ))}
          </select>
          {/* Map is centered at {mapCenter[0]}, {mapCenter[1]} */}
          {map}
    </div>
  );
}

function getIcon(type) {
  switch (type) {
    case "Supplier":
      return supplierIcon;
    case "Market":
      return marketIcon;
    case "Dry Port":
      return dryPortIcon;
    case "Origin/Dest":
      return originDestIcon;
    default:
      return supplierIcon;
  }
}
