import React, { useState } from "react";
import axios from "axios"; // Import axios for making HTTP requests
import "./styles.css";
import "leaflet/dist/leaflet.css";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import MarkerClusterGroup from "react-leaflet-cluster";
import { Icon, divIcon, point } from "leaflet";
import { coordinatesData } from "./coordinates"; // Import coordinatesData from coordinates.js

// Define icons for different types
const supplierIcon = new Icon({
  iconUrl: require("./icons/supplier-icon.png"),
  iconSize: [38, 38]
});

const marketIcon = new Icon({
  iconUrl: require("./icons/market-icon.png"),
  iconSize: [38, 38]
});

const dryPortIcon = new Icon({
  iconUrl: require("./icons/dry-port-icon.png"),
  iconSize: [38, 38]
});

const originDestIcon = new Icon({
  iconUrl: require("./icons/origin-dest-icon.png"),
  iconSize: [38, 38]
});

const createClusterCustomIcon = function (cluster) {
  return new divIcon({
    html: `<span class="cluster-icon">${cluster.getChildCount()}</span>`,
    className: "custom-marker-cluster",
    iconSize: point(33, 33, true)
  });
};

export default function App() {
  const [Text, setText] = useState(""); // State variable for the text input
  const [quantityValue, setQuantityValue] = useState(1); // State variable for the quantity input
  const [selectedType, setSelectedType] = useState(null);
  useEffect(() => {
    // Your code to fetch data or perform other side effects can go here
  }, [quantityValue]);
  const handleTypeFilter = (type) => {
    setSelectedType(type === selectedType ? null : type);
  };

  const handleTextChange = (event) => {
    setText(event.target.value); // Update the Text state variable when the input value changes
  };

  const handleQuantityChange = (event) => {
    setQuantityValue(parseInt(event.target.value)); // Update the quantityValue state variable when the input value changes
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Send speech text to the Flask backend
      const response = await axios.post('http://127.0.0.1:8000/your-backend-endpoint', { quantity: quantityValue, Text });
      console.log(response.data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // Filter markers based on the selected type
  const filteredMarkers = selectedType
    ? coordinatesData.filter((coordinate) => coordinate.type === selectedType)
    : coordinatesData;

  return (
    <div>
      <div className="button-container">
        <button onClick={() => handleTypeFilter("Supplier")}>Supplier</button>
        <button onClick={() => handleTypeFilter("Market")}>Market</button>
        <button onClick={() => handleTypeFilter("Dry Port")}>Dry Port</button>
        <button onClick={() => handleTypeFilter("Origin/Dest")}>Origin/Dest</button>
        <button onClick={() => setSelectedType(null)}>Show All</button>
      </div>
      <MapContainer center={[23.6850, 90.3563]} zoom={7} className="map-container">
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <MarkerClusterGroup
          chunkedLoading
          iconCreateFunction={createClusterCustomIcon}
        >
          {filteredMarkers.map((marker, index) => (
            <Marker key={index} position={[marker.latitude, marker.longitude]} icon={getIcon(marker.type)}>
              <Popup>
                <div>
                  <h3>{marker.name}</h3>
                  <form onSubmit={handleSubmit}>
                    <label htmlFor="product">Select Product:</label>
                    <select id="product" name="product">
                      <option value="Product 1">Product 1</option>
                      <option value="Product 2">Product 2</option>
                      <option value="Product 3">Product 3</option>
                      <option value="Product 4">Product 4</option>
                    </select>
                    <br></br>
                    <label htmlFor="quantity">Quantity:</label>
                    <input type="number" id="quantity" name="quantity" min="1" value={quantityValue} onChange={handleQuantityChange} />
                    <br></br>
                    <label htmlFor="text">Text:</label>
                    <input type="text" id="text" name="text" value={Text} onChange={handleTextChange} />
                    <button type="submit">Submit</button>
                  </form>
                </div>
              </Popup>
            </Marker>
          ))}
        </MarkerClusterGroup>
      </MapContainer>
    </div>
  );
}

// Function to get the appropriate icon based on the type
function getIcon(type) {
  switch (type) {
    case "Supplier":
      return supplierIcon;
    case "Market":
      return marketIcon;
    case "Dry Port":
      return dryPortIcon;
    case "Origin/Dest":
      return originDestIcon;
    default:
      return supplierIcon; // Default icon
  }
}
